import { Router } from "express";
import * as productoController from "../controllers/producto.controller";

const router = Router();

// Verifica que `productoController.getProductos` est correctamente importado y definido
router.get('/', productoController.getProductos);

// Asegrate de que todas las funciones estn definidas en `productoController`
router.post('/', productoController.createProducto);
router.delete('/:productoId', productoController.deleteProducto);
router.get('/terminados', productoController.getProductosTerminados);

export default router;

