sourcesContent:
import { Schema, model, Types } from "mongoose"

const componenteSchema = new Schema({
    name: {
      type: String,
      required: true
    },
    material: {
      type: Types.ObjectId,
      ref: 'Materiales',
      required: true
    },
    producto: {
      type: Types.ObjectId,
      ref: 'Productos',
      required: true
    },
    quantity: {
      type: Number,
      required: true
    }
  }, {
    timestamps: true,
    versionKey: false
  });

  export default model('Componentes', componenteSchema);

