sourcesContent:
import Componente from "../models/Componente";

export const getComponentes = async (req, res) => {

    const componenetes = await Componente.find({});
    console.log("Si llego al controlador")

    res.status(200).json(componenetes )
}

export const getComponenteById = async (req, res) => {

    if (!req.params.ComponenteId) {
        res.status(404).json({message: 'Id inexistente'})
    }
    
    if (req.params.ComponenteId.length<24) {
        res.status(404).json({message: 'Id invalido'})   
    }

    const Componente = await Componente.findById(req.params.ComponenteId );
    //console.log(componente)
    if (Componente) {
        res.status(200).json(Componente )

    } else{
        res.status(404).json({message: 'Componente no encontrado'})
    }

}

