import { Schema, model, models } from "mongoose";

const piezaSchema = new Schema({
  nombre: {
    type: String,
    required: true
  },
  descripcion: {
    type: String,
    required: false
  },
  cantidad: {
    type: Number,
    required: true,
    min: 0
  },
  stockMinimo: {
    type: Number,
    required: true,  
    default: 10      
  },
  seFabrica: { 
    type: String, 
    enum: ['interna', 'externa'],  // Define si la pieza se fabrica internamente o externamente
    required: true 
  }
}, {
  timestamps: true,
  versionKey: false
});

// Usa `models.Pieza` si ya est definido, de lo contrario, crea el modelo
const Pieza = models.Pieza || model('Pieza', piezaSchema);

export default Pieza;

